import React, { useState } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext';

const Navbar = () => {
  const { user, logout } = useAuth();
  const location = useLocation();
  const navigate = useNavigate();
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const handleLogout = () => {
    logout();
    navigate('/');
    setIsMenuOpen(false);
  };

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const isActive = (path) => {
    return location.pathname === path;
  };

  const closeMenu = () => {
    setIsMenuOpen(false);
  };

  return (
    <nav className="navbar">
      <div className="container">
        <div className="navbar-content">
          {/* Brand */}
          <Link to="/" className="navbar-brand" onClick={closeMenu}>
            üåç LinguaLearn
          </Link>

          {/* Desktop Navigation */}
          <div className="navbar-nav navbar-desktop">
            {user ? (
              // Authenticated user navigation
              <>
                <Link 
                  to="/dashboard" 
                  className={`nav-link ${isActive('/dashboard') ? 'active' : ''}`}
                >
                  Dashboard
                </Link>
                <Link 
                  to="/lessons" 
                  className={`nav-link ${isActive('/lessons') ? 'active' : ''}`}
                >
                  Lessons
                </Link>
                <Link 
                  to="/flashcards" 
                  className={`nav-link ${isActive('/flashcards') ? 'active' : ''}`}
                >
                  Flashcards
                </Link>
                <Link 
                  to="/quiz" 
                  className={`nav-link ${isActive('/quiz') ? 'active' : ''}`}
                >
                  Quiz
                </Link>
                <Link 
                  to="/vocabulary" 
                  className={`nav-link ${isActive('/vocabulary') ? 'active' : ''}`}
                >
                  Vocabulary
                </Link>
                <Link 
                  to="/progress" 
                  className={`nav-link ${isActive('/progress') ? 'active' : ''}`}
                >
                  Progress
                </Link>
                <Link 
                  to="/achievements" 
                  className={`nav-link ${isActive('/achievements') ? 'active' : ''}`}
                >
                  Achievements
                </Link>

                {/* User Menu */}
                <div className="user-menu">
                  <div className="user-info">
                    <span className="user-name text-sm font-medium">
                      {user.firstName} {user.lastName}
                    </span>
                    <div className="user-level">
                      <span className="badge badge-primary text-xs">
                        {user.learningLanguages?.[0]?.language || 'Beginner'}
                      </span>
                    </div>
                  </div>
                  
                  <div className="user-actions">
                    <Link 
                      to="/profile" 
                      className={`nav-link ${isActive('/profile') ? 'active' : ''}`}
                    >
                      Profile
                    </Link>
                    <Link 
                      to="/profile" 
                      className={`nav-link ${isActive('/profile') ? 'active' : ''}`}
                    >
                      Profile
                    </Link>
                    <button 
                      onClick={handleLogout}
                      className="btn btn-outline btn-sm"
                    >
                      Logout
                    </button>
                  </div>
                </div>
              </>
            ) : (
              // Public navigation
              <>
                <Link 
                  to="/login" 
                  className={`nav-link ${isActive('/login') ? 'active' : ''}`}
                >
                  Login
                </Link>
                <Link 
                  to="/register" 
                  className="btn btn-primary btn-sm"
                >
                  Get Started
                </Link>
              </>
            )}
          </div>

          {/* Mobile Menu Toggle */}
          <button 
            className="mobile-menu-toggle"
            onClick={toggleMenu}
            aria-label="Toggle mobile menu"
          >
            <span className={`hamburger ${isMenuOpen ? 'active' : ''}`}>
              <span></span>
              <span></span>
              <span></span>
            </span>
          </button>
        </div>
      </div>

      {/* Mobile Navigation */}
      {isMenuOpen && (
        <div className="mobile-menu">
          <div className="mobile-menu-content">
            {user ? (
              // Authenticated mobile menu
              <>
                <div className="mobile-user-info">
                  <div className="flex items-center gap-3 p-4 border-b border-gray-200">
                    <div className="user-avatar">
                      <div className="w-10 h-10 bg-primary-color text-white rounded-full flex items-center justify-center font-semibold">
                        {user.firstName?.charAt(0)}{user.lastName?.charAt(0)}
                      </div>
                    </div>
                    <div>
                      <div className="font-medium">{user.firstName} {user.lastName}</div>
                      <div className="text-sm text-secondary">
                        {user.email}
                      </div>
                    </div>
                  </div>
                </div>

                <div className="mobile-nav-links">
                  <Link 
                    to="/dashboard" 
                    className={`mobile-nav-link ${isActive('/dashboard') ? 'active' : ''}`}
                    onClick={closeMenu}
                  >
                    üìä Dashboard
                  </Link>
                  <Link 
                    to="/lessons" 
                    className={`mobile-nav-link ${isActive('/lessons') ? 'active' : ''}`}
